DROP TABLE IF EXISTS `cms_files`;

CREATE TABLE `cms_files` (
  `id` int NOT NULL COMMENT '主键',
  `user_id` int NOT NULL COMMENT '用户id',
  `share_status` char(2) NOT NULL DEFAULT '1' COMMENT '共享状态（0已分享 1未分享）',
